const statusCode = $json.statusCode ?? 0;
const ok = statusCode >= 200 && statusCode < 300;
const body = $json.body ?? {};
const analysisId = body?.id ?? null;
const analysisLink = body?.link ?? null;
const message = ok
  ? (body?.message || 'OK')
  : (body?.message || body?.data?.message || 'Errore upsert');

return [{
  json: {
    upsert_ok: ok,
    upsert_status_code: statusCode,
    upsert_message: message,
    analysis_id: analysisId,
    analysis_link: analysisLink,
    upsert_raw: body,
    upsert_at_iso: new Date().toISOString()
  }
}];

// ===== Helper =====
const toArray = (v) =>
  Array.isArray(v)
    ? v.map(x => (typeof x === 'string' ? x.trim() : x))
        .filter(x => x !== null && x !== undefined && String(x).trim() !== '')
    : [];

const toStringSafe = (v) => (v === null || v === undefined) ? '' : String(v).trim();

const toInt = (v) => {
  const n = Number.parseInt(v, 10);
  return Number.isFinite(n) ? n : null;
};

const clamp01 = (n) => (n === null ? null : Math.max(0, Math.min(100, n)));

const dedup = (arr) => [...new Set(arr.map(x => (typeof x === 'string' ? x.trim() : x)))];

const toBool = (v) => Boolean(v);

// Normalizza array di oggetti { rischio, mitigazione }
const normRischiMitigazioni = (arr) => {
  const a = Array.isArray(arr) ? arr : [];
  return a.map(x => {
      const o = (x && typeof x === 'object') ? x : {};
      const rischio = toStringSafe(o.rischio);
      const mitigazione = toStringSafe(o.mitigazione);
      return (rischio || mitigazione) ? { rischio, mitigazione } : null;
    })
    .filter(Boolean);
};

// Normalizza priorità temporali
const normPriorita = (obj) => {
  const o = (obj && typeof obj === 'object') ? obj : {};
  const entro_30_giorni = dedup(toArray(o.entro_30_giorni || []));
  const entro_90_giorni = dedup(toArray(o.entro_90_giorni || []));
  const entro_12_mesi   = dedup(toArray(o.entro_12_mesi   || []));
  return { entro_30_giorni, entro_90_giorni, entro_12_mesi };
};

// Normalizza prove trust
const normProveTrust = (obj) => {
  const o = (obj && typeof obj === 'object') ? obj : {};
  return {
    team_visibile:   toBool(o.team_visibile),
    case_study:      toBool(o.case_study),
    testimonianze:   toBool(o.testimonianze),
    certificazioni:  dedup(toArray(o.certificazioni || []))
  };
};

// Normalizza fonti {titolo?, url(required)}
const normFonti = (arr) => {
  const a = Array.isArray(arr) ? arr : [];
  return a.map(x => {
      const o = (x && typeof x === 'object') ? x : {};
      const url = toStringSafe(o.url);
      const titolo = toStringSafe(o.titolo || '');
      return url ? { titolo, url } : null;
    })
    .filter(Boolean);
};

// ===== 1) Input =====
const first = $input.first()?.json ?? {};
let content = first?.message?.content ?? first?.content ?? first;
let contentStr = typeof content === 'string' ? content : JSON.stringify(content);

// ===== 2) Parse JSON dell'agente =====
let parsedData;
try {
  parsedData = JSON.parse(contentStr);
} catch (e) {
  throw new Error('Content non è JSON valido: ' + e.message);
}

// ===== 3) Estrazione & Normalizzazione =====
// Executive & Brand
const riassunto              = toStringSafe(parsedData.riassunto);
const messaggi_principali    = dedup(toArray(parsedData.messaggi_principali));
const report_discorsivo      = toStringSafe(parsedData.report_discorsivo); // NEW FIELD
const promessa_valore        = toStringSafe(parsedData.promessa_valore);
const tono_di_voce           = toStringSafe(parsedData.tono_di_voce);
const differenziazione       = dedup(toArray(parsedData.differenziazione));
const coerenza_comunicativa  = toStringSafe(parsedData.coerenza_comunicativa);

// Core SWOT/Actions
const punti_forza            = dedup(toArray(parsedData.punti_forza));
const punti_debolezza        = dedup(toArray(parsedData.punti_debolezza));
const opportunita            = dedup(toArray(parsedData.opportunita));
const azioni_rapide          = dedup(toArray(parsedData.azioni_rapide));

// Target & Value
const target_commerciali     = dedup(toArray(parsedData.target_commerciali));
const potenziale             = toStringSafe(parsedData.potenziale);
const idee_valore_agenzia    = dedup(toArray(parsedData.idee_valore_agenzia));
const domande_prospect       = dedup(toArray(parsedData.domande_prospect));

// Attendibilità & Rischi
const attendibilita_dati_in  = (parsedData.attendibilita_dati && typeof parsedData.attendibilita_dati === 'object') ? parsedData.attendibilita_dati : {};
const attendibilita_dati     = {
  alert: Boolean(attendibilita_dati_in.alert),
  note:  toStringSafe(attendibilita_dati_in.note)
};
const rischi_mitigazioni     = normRischiMitigazioni(parsedData.rischi_mitigazioni);

// Priorità temporali
const priorita_temporali     = normPriorita(parsedData.priorita_temporali);

// Prove trust
const prove_trust            = normProveTrust(parsedData.prove_trust);

// Fonti
const fonti                  = normFonti(parsedData.fonti);

// Voti
const voto_qualita_analisi   = clamp01(toInt(parsedData.voto_qualita_analisi));
const voto_qualita_dati      = clamp01(toInt(parsedData.voto_qualita_dati));

// ===== 4) Conteggi =====
const count_messaggi_principali   = messaggi_principali.length;
const count_differenziazione      = differenziazione.length;

const count_punti_forza           = punti_forza.length;
const count_punti_debolezza       = punti_debolezza.length;
const count_opportunita           = opportunita.length;
const count_azioni_rapide         = azioni_rapide.length;

const count_target_commerciali    = target_commerciali.length;
const count_idee_valore_agenzia   = idee_valore_agenzia.length;
const count_domande_prospect      = domande_prospect.length;

const count_rischi_mitigazioni    = rischi_mitigazioni.length;

const count_priorita_30            = priorita_temporali.entro_30_giorni.length;
const count_priorita_90            = priorita_temporali.entro_90_giorni.length;
const count_priorita_12m           = priorita_temporali.entro_12_mesi.length;

const count_certificazioni         = prove_trust.certificazioni.length;

const count_fonti                  = fonti.length;

// Calcola lunghezza report_discorsivo
const lunghezza_report_discorsivo = report_discorsivo.length;

const totale_elementi =
  count_messaggi_principali +
  count_differenziazione +
  count_punti_forza +
  count_punti_debolezza +
  count_opportunita +
  count_azioni_rapide +
  count_target_commerciali +
  count_idee_valore_agenzia +
  count_domande_prospect +
  count_rischi_mitigazioni +
  count_priorita_30 + count_priorita_90 + count_priorita_12m +
  count_certificazioni +
  count_fonti;

// ===== 5) Payload WordPress (ACF) =====
// Adegua i field keys ACF se usi chiavi 'field_XXXX'
const wordpressData = {
  acf: {
    // Executive & Brand
    riassunto,
    messaggi_principali,
    count_messaggi_principali,
    report_discorsivo, // NEW FIELD - Testo medio per documento cliente
    promessa_valore,
    tono_di_voce,
    differenziazione,
    count_differenziazione,
    coerenza_comunicativa,

    // Core SWOT/Actions
    punti_forza,
    count_punti_forza,
    punti_debolezza,
    count_punti_debolezza,
    opportunita,
    count_opportunita,
    azioni_rapide,
    count_azioni_rapide,

    // Target & Value
    target_commerciali,
    count_target_commerciali,
    potenziale,
    idee_valore_agenzia,
    count_idee_valore_agenzia,
    domande_prospect,
    count_domande_prospect,

    // Attendibilità & Rischi
    attendibilita_dati, // {alert, note}
    rischi_mitigazioni, // repeater [{rischio, mitigazione}]
    count_rischi_mitigazioni,

    // Priorità temporali
    priorita_temporali, // {entro_30_giorni:[], entro_90_giorni:[], entro_12_mesi:[]}
    count_priorita_30,
    count_priorita_90,
    count_priorita_12_mesi: count_priorita_12m,

    // Prove trust
    prove_trust, // {team_visibile, case_study, testimonianze, certificazioni:[]}
    count_certificazioni,

    // Fonti
    fonti, // [{titolo?, url}]
    count_fonti,

    // Totali & Voti
    lunghezza_report_discorsivo, // NEW - Lunghezza in char del report
    totale_elementi,
    voto_qualita_analisi,
    voto_qualita_dati,

    // Audit
    output_agente_raw: contentStr
  }
};

// ===== 6) Return =====
return { json: wordpressData };

// ===== Parse & Merge: WAVE + Lighthouse (Microlink) - OPTIMIZED =====

const items = $input.all();
const output = [];

// Helper sicuro per array
const toArray = (val) => {
  if (Array.isArray(val)) return val;
  if (val && typeof val === 'object' && val.items && Array.isArray(val.items)) return val.items;
  return [];
};

// Identifica WAVE (primo item) e Microlink (secondo item)
const waveData = items[0]?.json || {};
const microlinkData = items[1]?.json || {};

// ========== PARSE WAVE ==========
const waveStats = waveData.statistics || {};
const waveCategories = waveData.categories || {};

// WAVE Errors
const waveErrors = [];
const waveErrorItems = waveCategories.error?.items || {};
for (const [errorId, errorData] of Object.entries(waveErrorItems)) {
  if (!errorData) continue;
  waveErrors.push({
    id: errorData.id || errorId,
    description: errorData.description || 'N/A',
    count: errorData.count || 0,
    selectors: Array.isArray(errorData.selectors) ? errorData.selectors.slice(0, 3) : [],
    severity: 'error',
    wcag: [] // WAVE non fornisce WCAG mapping diretto negli error items
  });
}

// WAVE Contrast Errors (categoria separata)
const contrastErrors = [];
const contrastItems = waveCategories.contrast?.items || {};
for (const [contrastId, contrastData] of Object.entries(contrastItems)) {
  if (!contrastData) continue;
  contrastErrors.push({
    id: contrastData.id || contrastId,
    description: contrastData.description || 'Contrast issue',
    count: contrastData.count || 0,
    selectors: Array.isArray(contrastData.selectors) ? contrastData.selectors.slice(0, 3) : [],
    contrastData: contrastData.contrastdata || [],
    severity: 'critical', // Contrast = sempre critical per EAA
    wcag: ['1.4.3'] // Contrast Minimum
  });
}

// WAVE Alerts
const waveAlerts = [];
const waveAlertItems = waveCategories.alert?.items || {};
for (const [alertId, alertData] of Object.entries(waveAlertItems)) {
  if (!alertData) continue;
  waveAlerts.push({
    id: alertData.id || alertId,
    description: alertData.description || 'N/A',
    count: alertData.count || 0,
    selectors: Array.isArray(alertData.selectors) ? alertData.selectors.slice(0, 3) : [],
    severity: 'warning'
  });
}

const waveErrorCount = waveStats.error || 0;
const waveContrastCount = waveCategories.contrast?.count || 0;
const waveAlertCount = waveStats.alert || 0;
const waveFeatureCount = waveStats.feature || 0;
const waveAriaCount = waveStats.aria || 0;
const waveStructureCount = waveStats.structure || 0;

// ========== PARSE LIGHTHOUSE (da Microlink) ==========
const lighthouseData = microlinkData.data?.insights?.lighthouse || {};
const lhCategories = lighthouseData.categories || {};
const lhAudits = lighthouseData.audits || {};

const lighthouseScore = lhCategories.accessibility?.score 
  ? Math.round(lhCategories.accessibility.score * 100) 
  : null;

// Critical WCAG audits
const criticalAudits = [
  'color-contrast', 'html-has-lang', 'meta-viewport', 'document-title',
  'link-name', 'button-name', 'image-alt', 'label', 'aria-valid-attr',
  'aria-required-attr', 'list', 'listitem', 'heading-order',
  'tabindex', 'target-size', 'video-caption', 'bypass'
];

const wcagMapping = {
  'color-contrast': '1.4.3 Contrast (Minimum)',
  'html-has-lang': '3.1.1 Language of Page',
  'meta-viewport': '1.4.4 Resize Text',
  'document-title': '2.4.2 Page Titled',
  'link-name': '2.4.4 Link Purpose',
  'button-name': '4.1.2 Name, Role, Value',
  'image-alt': '1.1.1 Non-text Content',
  'label': '3.3.2 Labels or Instructions',
  'aria-valid-attr': '4.1.2 Name, Role, Value',
  'aria-required-attr': '4.1.2 Name, Role, Value',
  'list': '1.3.1 Info and Relationships',
  'listitem': '1.3.1 Info and Relationships',
  'heading-order': '1.3.1 Info and Relationships',
  'tabindex': '2.4.3 Focus Order',
  'target-size': '2.5.8 Target Size',
  'video-caption': '1.2.2 Captions',
  'bypass': '2.4.1 Bypass Blocks'
};

const lhViolations = [];
const lhWarnings = [];
const lhPassed = [];

for (const [auditId, audit] of Object.entries(lhAudits)) {
  if (!audit || audit.score === null) continue;

  const isCritical = criticalAudits.includes(auditId);

  const auditData = {
    id: auditId,
    title: audit.title || 'N/A',
    description: audit.description || '',
    score: audit.score,
    wcag_criterion: wcagMapping[auditId] || 'N/A'
  };

  // Parse details (SAFE)
  if (audit.details) {
    const detailItems = toArray(audit.details.items || audit.details);

    if (detailItems.length > 0) {
      auditData.failing_elements = detailItems.slice(0, 5).map(el => {
        if (!el) return { selector: 'N/A', snippet: 'N/A' };
        return {
          selector: el.node?.selector || el.selector || 'N/A',
          snippet: el.node?.snippet || el.snippet || 'N/A'
        };
      });
      auditData.total_violations = detailItems.length;
    } else {
      auditData.failing_elements = [];
      auditData.total_violations = 0;
    }
  }

  if (audit.score < 1) {
    if (audit.score === 0 && isCritical) {
      auditData.severity = 'critical';
      lhViolations.push(auditData);
    } else {
      auditData.severity = 'warning';
      lhWarnings.push(auditData);
    }
  } else if (isCritical) {
    lhPassed.push(auditData);
  }
}

// ========== COMBINED SCORE ==========
// WAVE ha più peso per EAA perché più specifico su WCAG
// WAVE: 70%, Lighthouse: 30%
const waveTotalIssues = waveErrorCount + waveContrastCount + (waveAlertCount * 0.5);
const wavePenalty = Math.min(100, waveTotalIssues * 3); // Ogni error/contrast vale 3 punti
const waveScore = Math.max(0, 100 - wavePenalty);

const combinedScore = lighthouseScore 
  ? Math.round((waveScore * 0.7) + (lighthouseScore * 0.3))
  : waveScore;

// EAA Compliance Level
let eaaCompliance = 'non_conforme';
if (combinedScore >= 90 && waveErrorCount === 0 && waveContrastCount === 0) {
  eaaCompliance = 'conforme_AA';
} else if (combinedScore >= 75 && waveErrorCount < 3 && waveContrastCount < 5) {
  eaaCompliance = 'parziale';
}

// ========== PRIORITÀ INTERVENTI ==========
const quickWins = [
  // WAVE quick wins (errors con pochi elementi)
  ...waveErrors.filter(e => e.count > 0 && e.count <= 5)
    .map(e => `${e.description} (${e.count} elementi) [WAVE]`),

  // Contrast con pochi elementi
  ...contrastErrors.filter(c => c.count <= 10)
    .map(c => `${c.description} (${c.count} elementi) - WCAG 1.4.3 [WAVE]`),

  // Lighthouse quick wins
  ...lhViolations.filter(v => v.total_violations > 0 && v.total_violations <= 10)
    .map(v => `${v.title} (${v.total_violations} elem) - ${v.wcag_criterion} [Lighthouse]`)
].slice(0, 10);

const majorIssues = [
  // WAVE major issues
  ...waveErrors.filter(e => e.count > 5)
    .map(e => `${e.description} (${e.count} elementi) [WAVE]`),

  // Contrast con molti elementi (CRITICO per EAA)
  ...contrastErrors.filter(c => c.count > 10)
    .map(c => `CRITICO: ${c.description} (${c.count} elementi) - WCAG 1.4.3 [WAVE]`),

  // Lighthouse major
  ...lhViolations.filter(v => v.total_violations > 10)
    .map(v => `${v.title} (${v.total_violations} elem) - ${v.wcag_criterion} [Lighthouse]`)
].slice(0, 10);

// ========== CRITICAL EAA BLOCKERS ==========
const eaaBlockers = [
  // Language missing = blocker Level A
  ...waveErrors.filter(e => e.id === 'language_missing')
    .map(e => `BLOCKER EAA: ${e.description} - WCAG 3.1.1 Level A`),

  // Empty headings/links = blocker
  ...waveErrors.filter(e => e.id === 'heading_empty' || e.id === 'link_empty')
    .map(e => `BLOCKER: ${e.description} (${e.count}x)`),

  // Contrast errors = blocker Level AA
  ...(waveContrastCount > 0 ? [`BLOCKER EAA: ${waveContrastCount} contrast errors - WCAG 1.4.3 Level AA`] : []),

  // HTML lang missing da Lighthouse
  ...(lhViolations.find(v => v.id === 'html-has-lang') ? ['BLOCKER: HTML lang attribute missing - WCAG 3.1.1'] : [])
];

// ========== OUTPUT COMBINATO ==========
output.push({
  json: {
    // Dati azienda
    company_data: $('Read Azienda WP').item.json,

    // Combined analysis
    combined_analysis: {
      accessibility_score: combinedScore,
      eaa_compliance: eaaCompliance,

      wave: {
        score: Math.round(waveScore),
        errors: waveErrorCount,
        contrast_errors: waveContrastCount,
        alerts: waveAlertCount,
        features: waveFeatureCount,
        aria: waveAriaCount,
        structure: waveStructureCount,
        aim_score: waveStats.AIMscore || null
      },

      lighthouse: {
        score: lighthouseScore,
        violations: lhViolations.length,
        warnings: lhWarnings.length,
        passed: lhPassed.length
      }
    },

    // Dettaglio violazioni
    violations: {
      wave_errors: waveErrors.slice(0, 15),
      wave_contrast: contrastErrors.slice(0, 10),
      wave_alerts: waveAlerts.slice(0, 15),
      lighthouse_critical: lhViolations.slice(0, 10),
      lighthouse_warnings: lhWarnings.slice(0, 10),
      eaa_blockers: eaaBlockers
    },

    // Priorità
    quick_wins: quickWins,
    major_issues: majorIssues,

    // Reports URLs
    reports: {
      wave_url: waveStats.waveurl || null,
      lighthouse_url: lighthouseData.reportUrl || null
    },

    // Metadata
    has_dual_audit: true,
    audit_timestamp: new Date().toISOString(),
    target_url: waveStats.pageurl || microlinkData.data?.url || null
  }
});

return output;
