// ===== Helper =====
const toArray = (v) =>
  Array.isArray(v)
    ? v.map(x => (typeof x === 'string' ? x.trim() : x))
        .filter(x => x !== null && x !== undefined && String(x).trim() !== '')
    : [];

const toStringSafe = (v) => (v === null || v === undefined) ? '' : String(v).trim();

const toInt = (v) => {
  const n = Number.parseInt(v, 10);
  return Number.isFinite(n) ? n : null;
};
const clamp01 = (n) => (n === null ? null : Math.max(0, Math.min(100, n)));
const dedup = (arr) => [...new Set(arr.map(x => (typeof x === 'string' ? x.trim() : x)))];
const toBool = (v) => Boolean(v);

// Normalizza array di oggetti { rischio, mitigazione }
const normRischiMitigazioni = (arr) => {
  const a = Array.isArray(arr) ? arr : [];
  return a.map(x => {
      const o = (x && typeof x === 'object') ? x : {};
      const rischio = toStringSafe(o.rischio);
      const mitigazione = toStringSafe(o.mitigazione);
      return (rischio || mitigazione) ? { rischio, mitigazione } : null;
    })
    .filter(Boolean);
};

// Normalizza priorità temporali
const normPriorita = (obj) => {
  const o = (obj && typeof obj === 'object') ? obj : {};
  const entro_30_giorni = dedup(toArray(o.entro_30_giorni || []));
  const entro_90_giorni = dedup(toArray(o.entro_90_giorni || []));
  const entro_12_mesi   = dedup(toArray(o.entro_12_mesi   || []));
  return { entro_30_giorni, entro_90_giorni, entro_12_mesi };
};

// Normalizza prove trust
const normProveTrust = (obj) => {
  const o = (obj && typeof obj === 'object') ? obj : {};
  return {
    team_visibile:   toBool(o.team_visibile),
    case_study:      toBool(o.case_study),
    testimonianze:   toBool(o.testimonianze),
    certificazioni:  dedup(toArray(o.certificazioni || []))
  };
};

// Normalizza fonti {titolo?, url(required)}
const normFonti = (arr) => {
  const a = Array.isArray(arr) ? arr : [];
  return a.map(x => {
      const o = (x && typeof x === 'object') ? x : {};
      const url = toStringSafe(o.url);
      const titolo = toStringSafe(o.titolo || '');
      return url ? { titolo, url } : null;
    })
    .filter(Boolean);
};

// ===== 1) Input =====
const first = $input.first()?.json ?? {};
let content = first?.message?.content ?? first?.content ?? first;
let contentStr = typeof content === 'string' ? content : JSON.stringify(content);

// ===== 2) Parse JSON dell’agente =====
let parsedData;
try {
  parsedData = JSON.parse(contentStr);
} catch (e) {
  throw new Error('Content non è JSON valido: ' + e.message);
}

// ===== 3) Estrazione & Normalizzazione =====
// Executive & Brand
const riassunto              = toStringSafe(parsedData.riassunto);
const messaggi_principali    = dedup(toArray(parsedData.messaggi_principali));
const promessa_valore        = toStringSafe(parsedData.promessa_valore);
const tono_di_voce           = toStringSafe(parsedData.tono_di_voce);
const differenziazione       = dedup(toArray(parsedData.differenziazione));
const coerenza_comunicativa  = toStringSafe(parsedData.coerenza_comunicativa);

// Core SWOT/Actions
const punti_forza            = dedup(toArray(parsedData.punti_forza));
const punti_debolezza        = dedup(toArray(parsedData.punti_debolezza));
const opportunita            = dedup(toArray(parsedData.opportunita));
const azioni_rapide          = dedup(toArray(parsedData.azioni_rapide));

// Target & Value
const target_commerciali     = dedup(toArray(parsedData.target_commerciali));
const potenziale             = toStringSafe(parsedData.potenziale);
const idee_valore_agenzia    = dedup(toArray(parsedData.idee_valore_agenzia));
const domande_prospect       = dedup(toArray(parsedData.domande_prospect));

// Attendibilità & Rischi
const attendibilita_dati_in  = (parsedData.attendibilita_dati && typeof parsedData.attendibilita_dati === 'object') ? parsedData.attendibilita_dati : {};
const attendibilita_dati     = {
  alert: Boolean(attendibilita_dati_in.alert),
  note:  toStringSafe(attendibilita_dati_in.note)
};
const rischi_mitigazioni     = normRischiMitigazioni(parsedData.rischi_mitigazioni);

// Priorità temporali
const priorita_temporali     = normPriorita(parsedData.priorita_temporali);

// Prove trust
const prove_trust            = normProveTrust(parsedData.prove_trust);

// Fonti
const fonti                  = normFonti(parsedData.fonti);

// Voti
const voto_qualita_analisi   = clamp01(toInt(parsedData.voto_qualita_analisi));
const voto_qualita_dati      = clamp01(toInt(parsedData.voto_qualita_dati));

// ===== 4) Conteggi =====
const count_messaggi_principali   = messaggi_principali.length;
const count_differenziazione      = differenziazione.length;

const count_punti_forza           = punti_forza.length;
const count_punti_debolezza       = punti_debolezza.length;
const count_opportunita           = opportunita.length;
const count_azioni_rapide         = azioni_rapide.length;

const count_target_commerciali    = target_commerciali.length;
const count_idee_valore_agenzia   = idee_valore_agenzia.length;
const count_domande_prospect      = domande_prospect.length;

const count_rischi_mitigazioni    = rischi_mitigazioni.length;

const count_priorita_30            = priorita_temporali.entro_30_giorni.length;
const count_priorita_90            = priorita_temporali.entro_90_giorni.length;
const count_priorita_12m           = priorita_temporali.entro_12_mesi.length;

const count_certificazioni         = prove_trust.certificazioni.length;

const count_fonti                  = fonti.length;

const totale_elementi =
  count_messaggi_principali +
  count_differenziazione +
  count_punti_forza +
  count_punti_debolezza +
  count_opportunita +
  count_azioni_rapide +
  count_target_commerciali +
  count_idee_valore_agenzia +
  count_domande_prospect +
  count_rischi_mitigazioni +
  count_priorita_30 + count_priorita_90 + count_priorita_12m +
  count_certificazioni +
  count_fonti;

// ===== 5) Payload WordPress (ACF) =====
// Adegua i field keys ACF se usi chiavi 'field_XXXX'
const wordpressData = {
  acf: {
    // Executive & Brand
    riassunto,
    messaggi_principali,
    count_messaggi_principali,
    promessa_valore,
    tono_di_voce,
    differenziazione,
    count_differenziazione,
    coerenza_comunicativa,

    // Core SWOT/Actions
    punti_forza,
    count_punti_forza,
    punti_debolezza,
    count_punti_debolezza,
    opportunita,
    count_opportunita,
    azioni_rapide,
    count_azioni_rapide,

    // Target & Value
    target_commerciali,
    count_target_commerciali,
    potenziale,
    idee_valore_agenzia,
    count_idee_valore_agenzia,
    domande_prospect,
    count_domande_prospect,

    // Attendibilità & Rischi
    attendibilita_dati, // {alert, note}
    rischi_mitigazioni, // repeater [{rischio, mitigazione}]
    count_rischi_mitigazioni,

    // Priorità temporali
    priorita_temporali, // {entro_30_giorni:[], entro_90_giorni:[], entro_12_mesi:[]}
    count_priorita_30,
    count_priorita_90,
    count_priorita_12_mesi: count_priorita_12m,

    // Prove trust
    prove_trust, // {team_visibile, case_study, testimonianze, certificazioni:[]}
    count_certificazioni,

    // Fonti
    fonti, // [{titolo?, url}]
    count_fonti,

    // Totali & Voti
    totale_elementi,
    voto_qualita_analisi,
    voto_qualita_dati,

    // Audit
    output_agente_raw: contentStr
  }
};

// ===== 6) Return =====
return { json: wordpressData };

