// ===== Helper =====
const toArray = (v) => Array.isArray(v) ? v.filter(x => x !== null && x !== undefined && String(x).trim() !== '') : [];
const toInt = (v) => {
  const n = Number.parseInt(v, 10);
  return Number.isFinite(n) ? n : null;
};
const clamp01 = (n) => n === null ? null : Math.max(0, Math.min(100, n));
const isPlainObject = (v) => v && typeof v === 'object' && !Array.isArray(v);

// ===== 1) Input =====
const first = $input.first()?.json ?? {};
let content = first?.message?.content ?? first?.content ?? first;
let contentStr = typeof content === 'string' ? content : JSON.stringify(content);

// ===== 2) Parse JSON dell’agente =====
let parsedData;
try {
  parsedData = JSON.parse(contentStr);
} catch (e) {
  throw new Error('Content non è JSON valido: ' + e.message);
}

// ===== 3) Normalizzazione base =====
const riassunto = (parsedData.riassunto ?? '').toString();
const punti_debolezza = toArray(parsedData.punti_debolezza);
const punti_forza     = toArray(parsedData.punti_forza);
const opportunita     = toArray(parsedData.opportunita);
const azioni_rapide   = toArray(parsedData.azioni_rapide);

const countDebolezze   = punti_debolezza.length;
const countForze       = punti_forza.length;
const countOpp         = opportunita.length;
const countAzioni      = azioni_rapide.length;
const totaleElementi   = countDebolezze + countForze + countOpp + countAzioni;

// ===== 4) Voti =====
const votoAnalisi = clamp01(toInt(parsedData.voto_qualita_analisi));
const votoDati    = clamp01(toInt(parsedData.voto_qualita_dati ?? parsedData.voto_qualita_input ?? parsedData.qualita_dati));

// ===== 5) Numeri chiave (nuova struttura: {label, dato, fonte}) =====
const numeriChiaveObj = isPlainObject(parsedData.numeri_chiave) ? parsedData.numeri_chiave : {};

// Repeater ACF: [ { chiave, label, dato, fonte } ]
const numeriChiaveRepeater = Object.entries(numeriChiaveObj).map(([chiave, voceRaw]) => {
  const voce = isPlainObject(voceRaw) ? voceRaw : {};
  return {
    chiave,                                   // es. "organic_ctr"
    label: (voce.label ?? chiave).toString(), // es. "CTR organico"
    dato:  (voce.dato ?? null),               // numero o stringa
    fonte: (voce.fonte ?? null)               // es. "dato sito", "analisi", "stima"
  };
});

// Conserva il raw per audit / dashboard future
let numeriChiaveRawStr = '{}';
try { numeriChiaveRawStr = JSON.stringify(numeriChiaveObj); } catch { /* ignore */ }

// ===== 6) Payload WordPress (ACF) =====
// Adegua i nomi ai tuoi field keys ACF (eventualmente 'field_XXXX').
const wordpressData = {
  acf: {
    riassunto,

    punti_debolezza,
    count_punti_debolezza: countDebolezze,

    punti_forza,
    count_punti_forza: countForze,

    opportunita,
    count_opportunita: countOpp,

    azioni_rapide,
    count_azioni_rapide: countAzioni,

    totale_elementi: totaleElementi,

    // Doppi voti
    voto_qualita_analisi: votoAnalisi,
    voto_qualita_dati: votoDati,

    // Numeri chiave (nuovo schema)
    numeri_chiave: numeriChiaveRepeater,
    numeri_chiave_count: numeriChiaveRepeater.length,
    numeri_chiave_raw: numeriChiaveRawStr,

    // Traccia l'output completo dell'agente per audit
    output_agente_raw: contentStr
  }
};

// ===== 7) Return =====
return { json: wordpressData };

