const statusCode = $json.statusCode ?? 0;
const ok = statusCode >= 200 && statusCode < 300;
const body = $json.body ?? {};
const analysisId = body?.id ?? null;
const analysisLink = body?.link ?? null;
const message = ok
  ? (body?.message || 'OK')
  : (body?.message || body?.data?.message || 'Errore upsert');

return [{
  json: {
    upsert_ok: ok,
    upsert_status_code: statusCode,
    upsert_message: message,
    analysis_id: analysisId,
    analysis_link: analysisLink,
    upsert_raw: body,
    upsert_at_iso: new Date().toISOString()
  }
}];
