// Parse JSON da GPT output
const raw = $input.first().json.output;
const data = JSON.parse(raw);

// Restituisci campi essenziali + key insights
return {
  json: {
    company_name_full: data.company_name_full,
    partita_iva: data.partita_iva,
    address: data.address,
    city: data.city,
    postal_code: data.postal_code,
    province: data.province,
    phone: data.phone,
    email: data.email,
    domain: data.domain,
    sector_specific: data.sector_specific,
    business_type: data.business_type,
    employee_count: data.employee_count,
    growth_stage: data.growth_stage,
    estimated_annual_revenue: data.estimated_annual_revenue,
    estimated_ebitda_percentage: data.estimated_ebitda_percentage,
    estimated_marketing_budget: data.estimated_marketing_budget,
    budget_tier: data.budget_tier,
    confidence_score: data.confidence_score,

    // Social
    linkedin_url: data.social_links.linkedin || '',
    facebook_url: data.social_links.facebook || '',
    instagram_url: data.social_links.instagram || '',
    x_url: data.social_links.x || '',
    youtube_url: data.social_links.youtube || '',
    tiktok_url: data.social_links.tiktok || '',
    social_links_json: JSON.stringify(data.social_links),

    // Key Insights (NUOVO)
    key_insights: data.key_insights || '',

    // Metadata
    enrichment_timestamp: new Date().toISOString(),
    enrichment_status: 'completed'
  }
};

// Rimuove tutto il contenuto prima e incluso il tag </think>
const output = $input.first().json.choices[0].message.content;
const cleanedText = output.replace(/[\s\S]*?<\/think>\s*/g, '').trim();

return {
  output: cleanedText
};
